        "Constructor": "function (self, unitId, unitFrame, envTable, modTable)\n    modTable.updateInterruptId(unitFrame)\n    modTable.setCastColorFunction(unitFrame)\nend",
        "Initialization": "function (modTable)\n    \n    -- settings:\n    local noKickReadyColor = modTable.config.noKickReadyColor or \"blue\"\n    local kickReadyInTimeColor = modTable.config.kickReadyInTimeColor or \"purple\"\n    local kickOutOfRangeColor = modTable.config.kickOutOfRangeColor or \"orange\"\n    local kickOutOfRangeEnabled = modTable.config.kickOutOfRangeEnabled\n    local kickReadyInTimeGraceTime = modTable.config.kickReadyInTimeGraceTime or 0.5 --sec - additional time on the CD to account for reaction, lag, etc\n    local checkCovenant = true\n    local enableSigil = true\n    local showSpark = modTable.config.showSpark\n    local sparkColor = modTable.config.sparkColor\n    local sparkWidth = modTable.config.sparkWidth\n    local sparkTexture = modTable.config.sparkTexture\n    local textureChangeNotReady = modTable.config.textureChangeNotReady\n    local defaultTexture\n    local notReadyTexture\n    local LSM = LibStub:GetLibrary (\"LibSharedMedia-3.0\")\n    if not LSM then\n        textureChangeNotReady = false\n    else\n        defaultTexture = LSM:Fetch (\"statusbar\", Plater.db.profile.cast_statusbar_texture)\n        notReadyTexture = LSM:Fetch (\"statusbar\", modTable.config.notReadyTexture) or modTable.config.notReadyTexture ~= \"\" and modTable.config.notReadyTexture or defaultTexture\n    end\n    \n    local specialCastColorsEnabled = modTable.config.specialColorsEnabled\n    local specialColorsData = modTable.config.specialColors\n    local platerCastColorsEnabled = modTable.config.platerCastColorsEnabled\n    local specialColorsForNonInterruptibleEnabled = modTable.config.specialColorsForNonInterruptibleEnabled\n    local specialColors = {}\n    local additionalInterruptColors = {\n        [202137] = {Plater:ParseColors(\"greenyellow\")}, --sigil of silence\n        [31935] = {Plater:ParseColors(\"greenyellow\")}, --prot avenger's shield\n        --[375576] = {Plater:ParseColors(\"greenyellow\")}, --paladin kyrian bell\n    }\n    \n    if platerCastColorsEnabled or specialColorsForNonInterruptibleEnabled then\n        for id, colorData in pairs(Plater.db.profile.cast_colors) do\n            if colorData and colorData[1] and colorData[2] then\n                specialColors[tonumber(id) or id] = {Plater:ParseColors(colorData[2])}\n            end\n        end\n    end\n    if specialCastColorsEnabled or specialColorsForNonInterruptibleEnabled then\n        for id, color in pairs(specialColorsData) do\n            specialColors[tonumber(id) or id] = {Plater:ParseColors(color)}\n        end\n    end\n    local useSpecial = specialCastColorsEnabled or platerCastColorsEnabled\n    \n    \n    -- functions:\n    \n    -- determine interrupt spell according to spec\n    function modTable.updateInterruptIDs ()\n        local specId = \"N/A\"\n        if WOW_PROJECT_ID == WOW_PROJECT_MAINLINE then\n            specId = GetSpecializationInfo(GetSpecialization())\n        end\n        modTable.knownSpecID = specId\n        local interruptIDs = {} -- contains: [spellID] = <is player ability>, false meaning pet or weird workarounds needed\n        if WOW_PROJECT_ID == WOW_PROJECT_MAINLINE then\n            if Plater.PlayerClass == \"ROGUE\" then\n                interruptIDs[1766] = true\n            elseif Plater.PlayerClass == \"DEATHKNIGHT\" then\n                interruptIDs[47528] = true\n            elseif Plater.PlayerClass == \"DEMONHUNTER\" then\n                interruptIDs[183752] = true\n                if specId == 581 then\n                    interruptIDs[202137] = enableSigil\n                end\n            elseif Plater.PlayerClass == \"DRUID\" then\n                if specId == 103 or specId == 104 or specId == 105 then\n                    interruptIDs[106839] = true -- feral/guardian/resto\n                elseif specId == 102 then\n                    interruptIDs[78675] = true -- moonkin\n                end\n            elseif Plater.PlayerClass == \"HUNTER\" then\n                if specId == 255 then\n                    interruptIDs[187707] = true -- SV\n                else\n                    interruptIDs[147362] = true -- BM/MS\n                end\n            elseif Plater.PlayerClass == \"MONK\" then\n                interruptIDs[116705] = true --WW/BM/MW\n            elseif Plater.PlayerClass == \"PALADIN\" then\n                interruptIDs[96231] = true --prot/ret/holy\n                if specId == 66 then\n                    interruptIDs[31935] = true --prot avenger's shield\n                    if IsPlayerSpell(375576) then\n                        interruptIDs[375576] = true --Divine Toll\n                    end\n                end\n            elseif Plater.PlayerClass == \"SHAMAN\" then\n                interruptIDs[57994] = true\n            elseif Plater.PlayerClass == \"WARRIOR\" then\n                interruptIDs[6552] = true\n                if modTable.isTalentLearned(90307) then --Disrupting Shout\n                    interruptIDs[90307] = \"386071\"\n                end\n            elseif Plater.PlayerClass == \"MAGE\" then\n                interruptIDs[2139] = true\n            elseif Plater.PlayerClass == \"PRIEST\" then\n                if specId == 258 then\n                    interruptIDs[15487] = true\n                end\n            elseif Plater.PlayerClass == \"WARLOCK\" then\n                interruptIDs[119910] = 119898 --Spell Lock (command ability)\n                if IsPlayerSpell(108503) then --GoSac\n                    interruptIDs[132409] = 119898 --sacrificed Spell Lock (command ability)\n                end\n                if IsPlayerSpell(212619) then --Call Felhunter (PVP ability)\n                    interruptIDs[212619] = true\n                end\n                if specId == 266 then\n                    interruptIDs[119914] = 119898 --demo/fel guard  Axe Toss (command ability)\n                end\n            elseif Plater.PlayerClass == \"EVOKER\" then\n                interruptIDs[351338] = true --quell talent\n            end\n        else -- classic\n            if Plater.PlayerClass == \"ROGUE\" then\n                interruptIDs[1766] = true\n            elseif Plater.PlayerClass == \"DEATHKNIGHT\" then\n                interruptIDs[47528] = true\n                interruptIDs[47476] = true\n            elseif Plater.PlayerClass == \"DRUID\" then\n                interruptIDs[16979] = true\n                interruptIDs[80964] = true\n                interruptIDs[80965] = true\n            elseif Plater.PlayerClass == \"HUNTER\" then\n                interruptIDs[34490] = true\n            elseif Plater.PlayerClass == \"SHAMAN\" then\n                interruptIDs[57994] = true\n            elseif Plater.PlayerClass == \"WARRIOR\" then\n                interruptIDs[6552] = true\n                interruptIDs[72] = true\n            elseif Plater.PlayerClass == \"MAGE\" then\n                interruptIDs[2139] = true\n            elseif Plater.PlayerClass == \"PRIEST\" then\n                interruptIDs[15487] = true\n            elseif Plater.PlayerClass == \"WARLOCK\" then\n                interruptIDs[19647] = true --Spell Lock\n            end\n        end\n        modTable.interruptSpellIDs = interruptIDs\n    end\n    \n    function modTable.updateInterruptId (unitFrame)\n        local specId = \"N/A\"\n        if WOW_PROJECT_ID == WOW_PROJECT_MAINLINE then\n            specId = GetSpecializationInfo(GetSpecialization())\n        end\n        if not modTable.interruptSpellIDs or modTable.knownSpecID ~= specId then\n            modTable.updateInterruptIDs()\n        end\n        unitFrame.castBar.interruptSpellIDs = modTable.interruptSpellIDs\n    end\n    \n    function modTable.isTalentLearned(nodeID)\n        if WOW_PROJECT_ID ~= WOW_PROJECT_MAINLINE then return false end\n        local talentConfig = C_ClassTalents.GetActiveConfigID()\n        local nodeInfo = talentConfig and nodeID and C_Traits.GetNodeInfo(talentConfig, nodeID)\n        return nodeInfo and nodeInfo.entryIDsWithCommittedRanks and nodeInfo.entryIDsWithCommittedRanks[1] and true or false\n    end\n    \n    local function isSpellOrTalentKnown(spellId)\n        if IsSpellKnown(spellId) or IsSpellKnown(spellId, true) then -- check pet as well\n            return true\n        elseif modTable.isTalentLearned(spellId) then\n            return true\n        end\n    end\n    \n    local function setCastBarTexture(castBar, notReady)\n        if textureChangeNotReady then\n            local texture = notReady and notReadyTexture or defaultTexture\n            if castBar.curTextureNotInterrupt ~= texture then\n                castBar:SetStatusBarTexture (texture)\n                castBar.curTextureNotInterrupt = texture\n            end\n        end\n    end\n    \n    local GetSpellCooldown = GetSpellCooldown or function(spellID)\n        local spellCooldownInfo = C_Spell.GetSpellCooldown(spellID)\n        if spellCooldownInfo then\n            return spellCooldownInfo.startTime, spellCooldownInfo.duration, spellCooldownInfo.isEnabled, spellCooldownInfo.modRate\n        end\n    end\n    local IsSpellInRange = IsSpellInRange and function(id) return IsSpellInRange(GetSpellInfo(id)) end or function(id, unit)\n        local result = C_Spell.IsSpellInRange(id, unit)\n        if result == true then\n            return 1\n        elseif result == false then\n            return 0\n        end\n        return nil\n    end\n    function modTable.setCastColorFunction (unitFrame)\n        -- create spark (if needed)\n        local castBar = unitFrame.castBar\n        if not castBar.InterruptSpark then\n            castBar.InterruptSpark = castBar:CreateTexture (nil, \"overlay\")\n            castBar.InterruptSpark:SetDrawLayer (\"overlay\", 4)\n            castBar.InterruptSpark:SetBlendMode (\"ADD\")\n            castBar.InterruptSpark:SetHeight(castBar:GetHeight() + 6)\n        end\n        castBar.InterruptSpark:SetTexture(Plater.SparkTextures[sparkTexture])\n        castBar.InterruptSpark:SetVertexColor(Plater:ParseColors(sparkColor))\n        castBar.InterruptSpark:SetWidth(sparkWidth)\n        \n        if not LSM then\n            textureChangeNotReady = false\n        else\n            defaultTexture = LSM:Fetch (\"statusbar\", Plater.db.profile.cast_statusbar_texture)\n            notReadyTexture = LSM:Fetch (\"statusbar\", modTable.config.notReadyTexture) or modTable.config.notReadyTexture ~= \"\" and modTable.config.notReadyTexture or defaultTexture\n        end\n        \n        -- overwrite the color function:\n        castBar.GetCastColor = function (self)\n            \n            self.InterruptSpark:Hide()\n            setCastBarTexture(self)\n            \n            local interruptCD, interruptReadyInTime = nil, false\n            local interruptDur, interruptStart = 0, 0\n            local usedInterruptSpellId\n            local spellOutOfRange = false --skip per default\n            if self.interruptSpellIDs and self.unit and UnitCanAttack(\"player\", self.unit) then\n                \n                for interruptSpellId, playerAbility in pairs(self.interruptSpellIDs) do\n                    if playerAbility == true or type(playerAbility) == \"string\"  then\n                        if isSpellOrTalentKnown(interruptSpellId) then\n                            interruptSpellId = tonumber(playerAbility) or interruptSpellId\n                            local cdStart, cdDur =  GetSpellCooldown(interruptSpellId)\n                            local tmpInterruptCD = (cdStart > 0 and  cdDur - (GetTime() - cdStart)) or 0\n                            --print(interruptSpellId, cdStart, cdDur, tmpInterruptCD)\n                            if not interruptCD or (tmpInterruptCD < interruptCD) then\n                                interruptCD = tmpInterruptCD\n                                interruptDur = cdDur\n                                interruptStart = cdStart\n                                spellOutOfRange = kickOutOfRangeEnabled and IsSpellInRange(interruptSpellId, self.unit) == 0\n                            elseif tmpInterruptCD == 0 then\n                                if additionalInterruptColors[interruptSpellId] or not usedInterruptSpellId then\n                                    usedInterruptSpellId = interruptSpellId\n                                elseif not additionalInterruptColors[interruptSpellId] and additionalInterruptColors[usedInterruptSpellId] then\n                                    usedInterruptSpellId = interruptSpellId\n                                end\n                            end\n                        end\n                    else\n                        if type(playerAbility) == \"number\" then\n                            -- workaround for spell overrides (on WL command ability, etc.)\n                            if FindSpellOverrideByID(playerAbility) == interruptSpellId then\n                                local cdStart, cdDur =  GetSpellCooldown(interruptSpellId)\n                                local tmpInterruptCD = (cdStart > 0 and  cdDur - (GetTime() - cdStart)) or 0\n                                if not interruptCD or  (tmpInterruptCD < interruptCD) then\n                                    interruptCD = tmpInterruptCD\n                                    interruptDur = cdDur\n                                    interruptStart = cdStart\n                                    spellOutOfRange = kickOutOfRangeEnabled and IsSpellInRange(interruptSpellId, self.unit) == 0\n                                elseif tmpInterruptCD == 0 then\n                                    if additionalInterruptColors[interruptSpellId] or not usedInterruptSpellId then\n                                        usedInterruptSpellId = interruptSpellId\n                                    elseif not additionalInterruptColors[interruptSpellId] and additionalInterruptColors[usedInterruptSpellId] then\n                                        usedInterruptSpellId = interruptSpellId\n                                    end\n                                end \n                            end\n                        end\n                    end\n                end\n                \n                if not interruptCD then\n                    interruptCD = 0\n                end\n                \n                if self.channeling then\n                    interruptReadyInTime = (interruptCD + kickReadyInTimeGraceTime) < self.value\n                else\n                    interruptReadyInTime = (interruptCD + kickReadyInTimeGraceTime) < (self.maxValue - self.value)\n                end\n            end\n            \n            if not interruptCD then\n                interruptCD = 0\n            end\n            \n            if (not self.canInterrupt) then\n                return (specialColorsForNonInterruptibleEnabled and (specialColors[self.spellName] or specialColors[self.spellID])) or self.Colors.NonInterruptible\n                \n            elseif (self.failed) then\n                return self.Colors.Failed\n                \n            elseif (self.interrupted) then\n                return self.Colors.Interrupted\n                \n            elseif (self.finished) then\n                return self.Colors.Finished\n                \n            elseif interruptCD > 0 and interruptReadyInTime then\n                if showSpark then\n                    self.InterruptSpark:Show()\n                    local sparkPosition = (interruptStart + interruptDur - self.spellStartTime + kickReadyInTimeGraceTime)/self.maxValue\n                    if self.channeling then\n                        sparkPosition = 1-sparkPosition\n                    end\n                    self.InterruptSpark:SetPoint (\"center\", self, \"left\", sparkPosition  * self:GetWidth(), 0)\n                end\n                setCastBarTexture(self, true)\n                return additionalInterruptColors[usedInterruptSpellId] or kickReadyInTimeColor\n                \n            elseif interruptCD > 0 then\n                setCastBarTexture(self, true)\n                return noKickReadyColor\n                \n            elseif kickOutOfRangeEnabled and spellOutOfRange then\n                return kickOutOfRangeColor\n                \n            elseif (self.channeling) then\n                return (useSpecial and (specialColors[self.spellName] or specialColors[self.spellID])) or self.Colors.Channeling\n                \n            else\n                return (useSpecial and (specialColors[self.spellName] or specialColors[self.spellID])) or self.Colors.Casting\n                \n            end\n        end\n    end\n    \n    modTable.lastUpdateTime = time()\n    \nend",
        "Nameplate Added": "function (self, unitId, unitFrame, envTable, modTable)\n    modTable.updateInterruptId(unitFrame)\nend\n\n\n",
        "Player Talent Update": "function (self, unitId, unitFrame, envTable, modTable)\n    modTable.updateInterruptIDs()\n    modTable.updateInterruptId(unitFrame)\nend\n\n\n"
      },
