        "Constructor": "function (self, unitId, unitFrame, envTable, modTable)\n    modTable.updateInterruptId(unitFrame)\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
        "Initialization": "function (modTable)\n    \n    local checkCovenant = true\n    \n    -- functions for interrupt ids\n    \n    -- determine interrupt spell according to spec\n    function modTable.updateInterruptIDs ()\n        local specId = GetSpecializationInfo(GetSpecialization())\n        modTable.knownSpecID = specId\n        local interruptIDs = {} -- contains: [spellID] = <is player ability>, false meaning pet or weird workarounds needed\n        if Plater.PlayerClass == \"ROGUE\" then\n            interruptIDs[1766] = true\n        elseif Plater.PlayerClass == \"DEATHKNIGHT\" then\n            interruptIDs[47528] = true\n        elseif Plater.PlayerClass == \"DEMONHUNTER\" then\n            interruptIDs[183752] = true\n        elseif Plater.PlayerClass == \"DRUID\" then\n            if specId == 103 or specId == 104 then\n                interruptIDs[106839] = true -- feral/guardian\n            elseif specId == 102 then\n                interruptIDs[78675] = true -- moonkin\n            end\n        elseif Plater.PlayerClass == \"HUNTER\" then\n            if specId == 255 then\n                interruptIDs[187707] = true -- SV\n            else\n                interruptIDs[147362] = true -- BM/MS\n            end\n        elseif Plater.PlayerClass == \"MONK\" then\n            interruptIDs[116705] = true\n        elseif Plater.PlayerClass == \"PALADIN\" then\n            if specId == 66 or specId == 70 then\n                interruptIDs[96231] = true --prot/ret\n            end\n            if specId == 66 then\n                interruptIDs[31935] = true --prot\n                if checkCovenant and IsPlayerSpell(304971) then\n                    interruptIDs[304971] = true --kyrian\n                end\n            end\n        elseif Plater.PlayerClass == \"SHAMAN\" then\n            interruptIDs[57994] = true\n        elseif Plater.PlayerClass == \"WARRIOR\" then\n            interruptIDs[6552] = true\n        elseif Plater.PlayerClass == \"MAGE\" then\n            interruptIDs[2139] = true\n        elseif Plater.PlayerClass == \"PRIEST\" then\n            if specId == 258 then\n                interruptIDs[15487] = true\n            end\n        elseif Plater.PlayerClass == \"WARLOCK\" then\n            interruptIDs[119910] = 119898 --Spell Lock (command ability)\n            if IsPlayerSpell(108503) then --GoSac\n                interruptIDs[132409] = 119898 --sacrificed Spell Lock (command ability)\n            end\n            if specId == 266 then\n                interruptIDs[119914] = 119898 --demo/fel guard  Axe Toss (command ability)\n            end\n        elseif Plater.PlayerClass == \"EVOKER\" then\n            interruptIDs[351338] = true\n        end\n        modTable.interruptSpellIDs = interruptIDs\n    end\n    \n    function modTable.updateInterruptId (unitFrame)\n        local specId = GetSpecializationInfo(GetSpecialization())\n        if not modTable.interruptSpellIDs or modTable.knownSpecID ~= specId then\n            modTable.updateInterruptIDs()\n        end\n        unitFrame.castBar.interruptSpellIDs = modTable.interruptSpellIDs\n    end\n    \n    ----------------------------------------------------------------\n    \n    -- settings for icon\n    local iconAnchor = {side = modTable.config.anchor, x = modTable.config.xOffset, y = modTable.config.yOffset}\n    local spellNameXOffset = Plater.db.profile.plate_config.enemynpc.spellname_text_anchor.x\n    local spellNameYOffset = Plater.db.profile.plate_config.enemynpc.spellname_text_anchor.y\n    local spellNameAnchorSide = Plater.db.profile.plate_config.enemynpc.spellname_text_anchor.side\n    if (modTable.config.useIconAnchorForSpellName) then\n        spellNameAnchorSide = modTable.config.anchor\n    end\n    local notReadyTexture = modTable.config.notReadyTexture\n    local readyInTimeTexture = modTable.config.readyInTimeTexture\n    local readyInTimeColor = modTable.config.readyInTimeColor\n    local notReadyColor = modTable.config.notReadyColor\n    \n    local kickReadyInTimeGraceTime = modTable.config. kickReadyInTimeGraceTime or 0.5 --sec\n    local includeCastTarget = modTable.config.includeCastTarget\n    \n    local testIcon = modTable.config.testIcon\n    \n    local function FormatCastName(unitId, castBar, availableSpace)\n        if (not includeCastTarget) then\n            DetailsFramework:TruncateText (castBar.Text, availableSpace)\n            return\n        end\n        \n        local spellNameSizeRatio = 0.60\n        \n        if castBar.IsInterrupted then\n            return\n        end\n        \n        local targetUnitId = UnitName (unitId .. \"target\");\n        if (not UnitExists(targetUnitId)) then\n            return\n        end\n        \n        local targetName = UnitName(targetUnitId)\n        if (not targetName) then\n            return\n        end\n        \n        castBar.Text:SetText(castBar.SpellName)\n        DetailsFramework:TruncateText (castBar.Text, availableSpace * spellNameSizeRatio)\n        \n        local currentText = castBar.Text:GetText() or \"\"\n        local targetNameByColor = Plater.SetTextColorByClass (targetUnitId, targetName)\n        castBar.Text:SetText(currentText .. \"  \" .. targetNameByColor .. \"\")\n        DetailsFramework:TruncateText (castBar.Text, availableSpace)\n    end\n    \n    local function GetInterruptStateOfCast (castBar)\n        \n        local interruptCD, interruptReadyInTime = nil, false\n        if modTable.interruptSpellIDs and UnitCanAttack(\"player\", castBar.unit) then\n            \n            for interruptSpellId, playerAbility in pairs(castBar.interruptSpellIDs) do\n                if playerAbility == true then\n                    \n                    if IsSpellKnown(interruptSpellId) then\n                        local cdStart, cdDur =  GetSpellCooldown(interruptSpellId)\n                        local tmpInterruptCD = (cdStart > 0 and  cdDur - (GetTime() - cdStart)) or 0\n                        if not interruptCD or (tmpInterruptCD < interruptCD) then\n                            interruptCD = tmpInterruptCD\n                        end\n                    end\n                else\n                    if type(playerAbility) == \"number\" and FindSpellOverrideByID(playerAbility) then\n                        local cdStart, cdDur =  GetSpellCooldown(interruptSpellId)\n                        local tmpInterruptCD = (cdStart > 0 and  cdDur - (GetTime() - cdStart)) or 0\n                        -- workaround for WL: assume you only have one intterrupt, so check if the CD is actually > 0. defaults to 0 later\n                        if tmpInterruptCD > 0 and (not interruptCD or (tmpInterruptCD < interruptCD)) then\n                            interruptCD = tmpInterruptCD\n                        end\n                    end\n                end\n            end\n            \n            if not interruptCD then\n                interruptCD = 0\n            end\n            \n            if castBar.channeling then\n                interruptReadyInTime = (interruptCD + kickReadyInTimeGraceTime) < castBar.value\n            else\n                interruptReadyInTime = (interruptCD + kickReadyInTimeGraceTime) < (castBar.maxValue - castBar.value)\n            end\n        end\n        \n        if not interruptCD then\n            interruptCD = 0\n        end\n        \n        if (testIcon) then\n            return \"showReadyInTime\"\n        end\n        \n        if (not castBar.canInterrupt) then\n            return \"noshow\"\n        elseif (castBar.failed) then\n            return \"noshow\"\n        elseif (castBar.interrupted) then\n            return \"noshow\"\n        elseif (castBar.finished) then\n            return \"noshow\"\n        elseif interruptCD > 0 and interruptReadyInTime then\n            return \"showReadyInTime\"\n        elseif interruptCD > 0 then\n            return \"showNotReady\"\n        elseif (castBar.channeling) then\n            return \"noshow\"\n        else        \n            return \"noshow\"\n        end\n    end\n    \n    function modTable.UpdateCast (unitId, unitFrame)\n        local castBar = unitFrame.castBar\n        local castBarHeight = castBar:GetHeight()\n        local castBarWidth = castBar:GetWidth()\n        \n        castBar.KickIcon:SetSize (castBarHeight, castBarHeight)\n        FormatCastName(unitId, castBar, castBarWidth)\n        Plater.SetAnchor(castBar.Text, {side = spellNameAnchorSide, x = spellNameXOffset, y = spellNameYOffset})\n        Plater.SetAnchor(unitFrame.castBar.KickIcon, iconAnchor)\n        castBar.KickIcon:Hide()\n        if (not Plater.IsInCombat()) then --not in combat\n            return\n        elseif (unitFrame.IsSelf) then --personal bar\n            return\n        end\n        \n        local iconStatus = GetInterruptStateOfCast (castBar)\n        \n        if (iconStatus == \"showReadyInTime\" or iconStatus == \"showNotReady\") then\n            if (modTable.config.anchor == spellNameAnchorSide) then\n                if (modTable.config.anchor == 10 or modTable.config.anchor == 3 or modTable.config.anchor == 1 or modTable.config.anchor == 6) then\n                    local truncatedCastBarWidth = castBarWidth - castBarHeight - spellNameXOffset - modTable.config.xOffset\n                    FormatCastName(unitId, castBar, truncatedCastBarWidth)\n                    Plater.SetAnchor(castBar.Text, {side = spellNameAnchorSide, x = castBarHeight + spellNameXOffset + modTable.config.xOffset, y = spellNameYOffset})\n                elseif (modTable.config.anchor == 11 or modTable.config.anchor == 5 or modTable.config.anchor == 7 or modTable.config.anchor == 2) then\n                    local truncatedCastBarWidth = castBarWidth - castBarHeight + spellNameXOffset + modTable.config.xOffset\n                    FormatCastName(unitId, castBar, truncatedCastBarWidth)\n                    Plater.SetAnchor(castBar.Text, {side = spellNameAnchorSide, x = -castBarHeight + spellNameXOffset + modTable.config.xOffset, y = spellNameYOffset})\n                end\n            end\n        end\n        \n        if (iconStatus == \"showReadyInTime\") then\n            castBar.KickIcon:SetVertexColor (unpack (readyInTimeColor))\n            castBar.KickIcon:SetTexture (readyInTimeTexture)\n            castBar.KickIcon:Show()\n        elseif(iconStatus == \"showNotReady\") then\n            castBar.KickIcon:SetVertexColor (unpack (notReadyColor))\n            castBar.KickIcon:SetTexture (notReadyTexture)\n            castBar.KickIcon:Show()\n        end\n    end\n    \n    function modTable.ConstructIcon(unitFrame)\n        unitFrame.castBar.KickIcon = unitFrame.castBar.KickIcon or unitFrame.castBar:CreateTexture (nil, \"overlay\")\n        Plater.SetAnchor(unitFrame.castBar.KickIcon, iconAnchor)\n        \n        unitFrame.castBar.KickIcon.Animate =  unitFrame.castBar.KickIcon.Animate or Plater:CreateAnimationHub (unitFrame.castBar.KickIcon, \n            function()\n            end,\n            function()\n            end\n        )\n        \n        -- optional spin animation component        \n        unitFrame.castBar.KickIcon.Animate.scaleUp = unitFrame.castBar.KickIcon.Animate.scaleUp or Plater:CreateAnimation (unitFrame.castBar.KickIcon.Animate, \"Scale\", 1, 0.15, 0, 0, 2, 2)\n        \n        unitFrame.castBar.KickIcon.Animate.wait = unitFrame.castBar.KickIcon.Animate.wait or Plater:CreateAnimation (unitFrame.castBar.KickIcon.Animate, \"Rotation\", 2, 0.3, 0)\n        \n        unitFrame.castBar.KickIcon.Animate.scaleDown = unitFrame.castBar.KickIcon.Animate.scaleDown or Plater:CreateAnimation (unitFrame.castBar.KickIcon.Animate, \"Scale\", 3, 0.1, 1, 1, 0.5, 0.5)\n        \n        --unitFrame.castBar.KickIcon.Animate.spin = unitFrame.castBar.KickIcon.Animate.spin or Plater:CreateAnimation (unitFrame.castBar.KickIcon.Animate, \"Rotation\", 3, 0.2, -360)\n    end\n    \nend",
