     end\n            else\n                unitFrame.healthBar.border:SetBorderSizes (normalThickness, normalThickness, normalThickness, normalThickness)\n                unitFrame.healthBar.border:UpdateSizes()\n            end\n        end        \n    end\n    \n    function envTable.Recolor (unitFrame, envTable)\n        local colorTable = Plater.GetNpcColor (unitFrame)\n        if colorTable then\n            --Plater.SetBorderColor (unitFrame, colorTable)\n            --unitFrame.healthBar.border:SetBorderSizes (3, 3, 3, 3)\n            --unitFrame.healthBar.border:UpdateSizes()\n        end\n        \n        --Plater.UpdateNameplateThread(unitFrame)\n        --Plater.FindAndSetNameplateColor (unitFrame, false)\n    end\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
