    {
      "Author": "Brewdux-Tarren Mill",
      "Desc": "Recolors the nameplate, if you the unit is tanked by a pet.",
      "Enabled": true,
      "Hooks": {
        "Constructor": "function (self, unitId, unitFrame, envTable)\n    envTable.color = \"#5d00ff\"\n    \n    envTable.npcs = {\n        [61056] = \"Greater Earth Elemental\",\n        [61146] = \"Black Ox Statue\",\n        [95072] = \"Earth Elemental\",\n        [103822] = \"Treant\"\n    }\n    \n    envTable.rules = {\n        [\"Pet\"] = true,\n        [\"Creature\"] = function(npcID)\n            return (envTable.npcs[npcID] ~= nil)\n        end\n    }\n    \n    envTable.getTypeAndID = function(guid)\n        local unitType, _, _, _, _, npcID = strsplit(\"-\", guid)\n        return unitType, tonumber(npcID or \"0\") or 0\n    end\n    \n    envTable.shallHighlight = function(self, guid)\n        local unitType, npcID = envTable.getTypeAndID(guid)\n        if unitType then\n            local value = envTable.rules[unitType]\n            if value then\n                if type(value) == \"boolean\" then\n                    return value\n                elseif type(value) == \"function\" then\n                    return value(npcID)\n                end\n            end -- value\n        end -- unitType\n    end\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
        "Nameplate Updated": "function (self, unitId, unitFrame, envTable)    \n    local inCombat = self.InCombat\n    local isTanking = self.namePlateThreatIsTanking\n    local isTapDenied = UnitIsTapDenied(unitId)\n    \n    if inCombat and not isTanking and not isTapDenied then\n        -- Krunde\n        if envTable.petTankUnit then\n            if envTable.petTankUnit == self.targetUnitID or unitFrame.castBar:IsVisible() then\n                return\n            end\n        else\n            local exists = UnitExists(self.targetUnitID)\n            if exists then\n                local role = UnitGroupRolesAssigned(self.targetUnitID)\n                local isTank = (role == \"TANK\")\n                \n                if not isTank then\n                    local guid = UnitGUID(self.targetUnitID)\n                    -- Krunde\n                    if envTable.shallHighlight(self, guid) then\n                        if not unitFrame.castBar:IsVisible() then\n                            envTable.petTankUnit = self.targetUnitID\n                            Plater.SetBorderColor(unitFrame, envTable.color)\n                            return\n                        end\n                    end\n                end\n            end\n        end\n    end\n    \n    -- Krunde\n    if envTable.petTankUnit then\n        envTable.petTankUnit = nil\n        Plater.SetBorderColor(unitFrame)\n        Plater.UpdateNameplateThread(unitFrame)\n    end\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n"
