        "Initialization": "function (modTable)\n    modTable.SetTextColor = function(text, color)\n        DF = _G.DetailsFramework\n        if color then\n            if text.originalColor == nil then\n                text.originalColor = {text:GetTextColor()}\n            end\n            DF:SetFontColor(text, color)\n        elseif text.originalColor then\n            DF:SetFontColor(text, text.originalColor)\n            text.originalColor = nil\n        end\n    end\n    \n    function modTable.BrandReColor (unitFrame, brand_recolor)\n        if not unitFrame or unitFrame.IsNpcWithoutHealthbar or unitFrame.IsFriendlyPlayerWithoutHealthbar then return\n        end \n        if brand_recolor == true then \n            --Plater.SetBorderColor (unitFrame, modTable.config.color)\n            modTable.SetTextColor(unitFrame.healthBar.lifePercent, modTable.config.color)\n        else\n            --Plater.SetBorderColor (unitFrame)\n            modTable.SetTextColor(unitFrame.healthBar.lifePercent)\n        end\n    end\nend",
        "Nameplate Updated": "function (self, unitId, unitFrame, envTable, modTable)\n    local brand_on_target = Plater.UnitHasAura (unitFrame, 207771)\n    if brand_on_target then\n        unitFrame.brand_recolor = true\n        modTable.BrandReColor (unitFrame, true)\n    else --no fiery brand on target\n        if unitFrame.brand_recolor == true then\n            --there was one, recoloring\n            unitFrame.brand_recolor = nil\n            modTable.BrandReColor (unitFrame, false)\n        end\n    end\nend"
